mapper.select:
select * from member
where member_email = #{member_email}
and member_pw = #{member_pw}

SELECT * 
FROM (
	SELECT ROWNUM RN, A.* 
		FROM (
				select * from member

					where member_name like '%'||#{search}||'%'

				) A
		)

	WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
update member set
member_yn = 'Y'
where member_num = #{member_num}

update member set
member_pw = '1234'
where member_num = #{member_num}
