mapper.select:
SELECT * 
FROM (
	SELECT ROWNUM RN, A.* 
		FROM (
				SELECT * 
				FROM section 

					where section_name = #{search}

				ORDER BY section_sort ASC 
				) A
		)
WHERE RN BETWEEN #{start} AND #{end}

select * from section
where section_id = #{section_id}
order by section_sort desc

mapper.insert:
insert into section (
	section_id,
	section_name,
	section_sort
)values(
	#{section_id},
	#{section_name},
	#{section_sort}
)

mapper.update:
update section set
	section_name = #{section_name},
	section_sort = #{section_sort}
where section_id = #{section_id}
